import React from 'react';
import { Sparkles, Heart, Lightbulb, Shield, Star, Clock } from 'lucide-react';

const ReadingResult = ({ reading, isLoading, asDialog = false, onClose, header }) => {
  if (isLoading) {
    return (
      <div className="glass-effect rounded-2xl p-8 max-w-4xl mx-auto animate-pulse">
        <div className="flex items-center justify-center mb-6">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-700"></div>
          <span className="ml-3 text-gray-800 text-lg">AIÊ≠£Âú®‰∏∫ÊÇ®Ëß£ËØª...</span>
        </div>
        <div className="space-y-4">
          {[1, 2, 3, 4].map((i) => (
            <div key={i} className="h-4 bg-black/10 rounded w-full"></div>
          ))}
        </div>
      </div>
    );
  }

  if (!reading) return null;

  // Ëß£ÊûêAIËøîÂõûÁöÑÊñáÊú¨ÔºàÊñ∞‰∫îÊÆµÁªìÊûÑ‰ºòÂÖàÔºåÂÖºÂÆπÊóßÁªìÊûÑÔºâ
  const parseReading = (text) => {
    const result = { overview: '', cards: '', timeline: '', advice: '', summary: '' };

    const headers = [
      { key: 'overview', regex: /(üí´\s*‰∏Ä„ÄÅÊï¥‰ΩìËß£ËØª|‰∏Ä„ÄÅÊï¥‰ΩìËß£ËØª)/ },
      { key: 'cards', regex: /(üåπ\s*‰∫å„ÄÅÈÄêÂº†Ëß£Êûê|‰∫å„ÄÅÈÄêÂº†Ëß£Êûê|„ÄêÂçïÂº†ÁâåËß£Êûê„Äë)/ },
      { key: 'timeline', regex: /(üï∞\s*‰∏â„ÄÅÊó∂Èó¥‰∏éÁªìÊûúË∂ãÂäø|‰∏â„ÄÅÊó∂Èó¥‰∏éÁªìÊûúË∂ãÂäø|Êó∂Èó¥È¢ÑÁ§∫)/ },
      { key: 'advice', regex: /(üíó\s*Âõõ„ÄÅÊåáÂºï‰∏éÂª∫ËÆÆ|Âõõ„ÄÅÊåáÂºï‰∏éÂª∫ËÆÆ|Ëß£ÂÜ≥ÊñπÂºè)/ },
      { key: 'summary', regex: /(‚ú®\s*ÊÄªÁªìÁªìËÆ∫|ÊÄªÁªìÁªìËÆ∫|ÊöñÂøÉÊøÄÂä±(?:ÁªìÂ∞æ)?|ÊÄªÁªì)/ }
    ];

    const markers = [];
    for (const h of headers) {
      const m = text.match(h.regex);
      if (m) markers.push({ key: h.key, label: m[0], idx: text.indexOf(m[0]) });
    }
    markers.sort((a, b) => a.idx - b.idx);

    if (markers.length) {
      // ÊääÁ¨¨‰∏Ä‰∏™Ê†áÈ¢òÂâçÁöÑÂÜÖÂÆπ‰Ωú‰∏∫ overview ÂÖúÂ∫ï
      if (markers[0].idx > 0) {
        result.overview = text.slice(0, markers[0].idx).trim();
      }
      for (let i = 0; i < markers.length; i++) {
        const start = markers[i].idx + markers[i].label.length;
        const end = i + 1 < markers.length ? markers[i + 1].idx : text.length;
        result[markers[i].key] = text.slice(start, end).trim();
      }
      return result;
    }

    // ÂÖºÂÆπÊóßÁöÑÊï∞Â≠óÂàÜÊÆµÁªìÊûÑ
    const legacy = text.split(/\n\s*\d+[„ÄÅ.]/).map(s => s.trim()).filter(Boolean);
    return {
      overview: legacy[0] || '',
      cards: legacy[1] || '',
      timeline: '',
      advice: legacy[2] || '',
      summary: legacy[3] || ''
    };
  };

  // Âü∫Á°ÄÊ∏ÖÁêÜ
  const sanitizedReading = reading.replace(/Âú®generalÊñπÈù¢[Ôºå,Ôºö:]?/g, '');
  const parsedRaw = parseReading(sanitizedReading);

  // Ê∏ÖÁêÜÊÆµËêΩÂºÄÂ§¥Â§ö‰ΩôÁöÑÁ¨¶Âè∑/ÁºñÂè∑ÔºàÂ¶ÇÂ≠§Á´ãÁöÑ":"„ÄÅ"3."Á≠âÔºâ
  const cleanSection = (text) => {
    if (!text) return '';
    const lines = text.split('\n');
    while (lines.length) {
      const first = lines[0].trim();
      if (first === '' || first === ':' || first === 'Ôºö' || /^\d+[\.„ÄÅ]$/.test(first)) {
        lines.shift();
        continue;
      }
      break;
    }
    let out = lines.join('\n');
    out = out.replace(/^\s*[:Ôºö]\s*/, '');
    return out.trim();
