import React, { useState, useEffect } from 'react';
import { Sparkles, RefreshCw, ArrowLeft, Maximize2, Minimize2, X } from 'lucide-react';
import ThemeButton from './components/ThemeButton';
import TarotCard from './components/TarotCard';
import ReadingResult from './components/ReadingResult';
import { themes, tarotCards } from './data/tarotCards';
import { OpenAIService } from './services/openaiService';
import { DemoService } from './services/demoService';

function App() {
  const [selectedTheme, setSelectedTheme] = useState(null);
  const [drawnCards, setDrawnCards] = useState([]);
  const [flippedCards, setFlippedCards] = useState(new Set());
  const [reading, setReading] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [showCards, setShowCards] = useState(false);
  const [emotionText, setEmotionText] = useState('');
  const [selectedSpread, setSelectedSpread] = useState('');
  const [analysisPattern, setAnalysisPattern] = useState('ËøáÂéª-Áé∞Âú®-Êú™Êù•');
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [followUpInput, setFollowUpInput] = useState('');
  const [showAllCards, setShowAllCards] = useState(false);
  const [selectedCardIds, setSelectedCardIds] = useState([]);
  const [cardsConfirmed, setCardsConfirmed] = useState(false);
  const [isInputExpanded, setIsInputExpanded] = useState(false);

  // ÊòæÁ§∫ÊâÄÊúâÁâå‰æõÁî®Êà∑ÈÄâÊã©
  const showCardSelection = () => {
    // Êâì‰π±ÊâÄÊúâÁâåÁöÑÈ°∫Â∫è
    const shuffled = [...tarotCards].sort(() => Math.random() - 0.5);
    // ‰∏∫ÊØèÂº†ÁâåÊ∑ªÂä†ÈöèÊú∫Ê≠£ÈÄÜ‰Ωç
    const cardsWithReversed = shuffled.map(c => ({
      ...c,
      reversed: Math.random() < 0.5
    }));
    // ËøôÈáåÊàë‰ª¨‰∏çÈúÄË¶ÅÂ≠òÂÇ®ÊâÄÊúâÁâåÔºåÂè™ÈúÄË¶ÅÊ†áËÆ∞ÊòæÁ§∫Áä∂ÊÄÅ
    setShowAllCards(true);
    setSelectedCardIds([]);
    setDrawnCards([]);
    setFlippedCards(new Set());
    setShowCards(false);
    setCardsConfirmed(false);
  };

  // Áî®Êà∑ÈÄâÊã©‰∏ÄÂº†Áâå
  const handleCardSelect = (cardId) => {
    if (selectedCardIds.includes(cardId)) {
      // Â¶ÇÊûúÂ∑≤ÈÄâ‰∏≠ÔºåÂàôÂèñÊ∂àÈÄâÊã©
      setSelectedCardIds(prev => prev.filter(id => id !== cardId));
    } else if (selectedCardIds.length < 3) {
      // Â¶ÇÊûúÊú™ÈÄâ‰∏≠‰∏îÊú™ÈÄâÊª°3Âº†ÔºåÂàôÊ∑ªÂä†
      setSelectedCardIds(prev => [...prev, cardId]);
    }
  };

  // Á°ÆËÆ§ÈÄâÊã©‰∏âÂº†Áâå
  const confirmCardSelection = () => {
    if (selectedCardIds.length !== 3) return;

    const selected = selectedCardIds.map((cardId, idx) => {
      const card = tarotCards.find(c => c.id === cardId);
      return {
        ...card,
        order: idx + 1,
        reversed: Math.random() < 0.5 // ‰∏∫ÈÄâ‰∏≠ÁöÑÁâåÈöèÊú∫ËÆæÁΩÆÊ≠£ÈÄÜ‰Ωç
      };
    });

    setDrawnCards(selected);
    setFlippedCards(new Set());
    setShowCards(true);
    setCardsConfirmed(true); // Ê†áËÆ∞Â∑≤ÂÆåÊàêÈÄâÊã©Ôºå‰ΩÜ‰∏çÈöêËóèÂúÜÂΩ¢ÊéíÂàó
  };

  // Â§ÑÁêÜ‰∏ªÈ¢òÈÄâÊã©
  const handleThemeSelect = (theme) => {
    setSelectedTheme(theme);
    setReading('');
    setShowCards(false);
    setFlippedCards(new Set());
  };

  // Â§ÑÁêÜÂç°ÁâáÁøªËΩ¨
  const handleCardFlip = (cardId) => {
    setFlippedCards(prev => {
      const newSet = new Set(prev);
      if (newSet.has(cardId)) {
        newSet.delete(cardId);
      } else {
        newSet.add(cardId);
      }
      return newSet;
    });
  };

  // Ëé∑ÂèñAIËß£ËØª
  const getAIReading = async () => {
    if (drawnCards.length === 0) return;

    setIsLoading(true);
    try {
      // Ê£ÄÊü•ÊòØÂê¶Êúâ AI API KeyÔºàÊîØÊåÅ DeepSeek„ÄÅOpenAI Á≠âÔºâ
      const apiKey1 = process.env.REACT_APP_AI_API_KEY;
      const apiKey2 = process.env.REACT_APP_OPENAI_API_KEY;
      const hasApiKey = (apiKey1 || apiKey2) && 
                       apiKey1 !== 'your_api_key_here' &&
                       apiKey2 !== 'your_openai_api_key_here';

      // Ë∞ÉËØï‰ø°ÊÅØ
      console.log('üîç API Key Ê£ÄÊµã:', {
        'REACT_APP_AI_API_KEY': apiKey1 ? `${apiKey1.substring(0, 10)}...` : 'Êú™ËÆæÁΩÆ',
        'REACT_APP_OPENAI_API_KEY': apiKey2 ? `${apiKey2.substring(0, 10)}...` : 'Êú™ËÆæÁΩÆ',
        'hasApiKey': hasApiKey,
        'Â∞Ü‰ΩøÁî®': hasApiKey ? 'ÁúüÂÆû API' : 'ÊºîÁ§∫Ê®°Âºè'
      });

      let result;
      if (hasApiKey) {
        result = await OpenAIService.generateTarotReading(selectedTheme?.id || 'general', drawnCards, {
          emotionText,
          spread: selectedSpread,
          analysisPattern
        });
      } else {
        // ‰ΩøÁî®ÊºîÁ§∫Ê®°Âºè
        result = await DemoService.generateTarotReading(selectedTheme?.id || 'general', drawnCards, {
          emotionText,
          spread: selectedSpread,
          analysisPattern
        });
      }
      setReading(result);
    } catch (error) {
      console.error('Ëé∑ÂèñËß£ËØªÂ§±Ë¥•:', error);
      setReading('Êä±Ê≠âÔºåËß£ËØªÊúçÂä°ÊöÇÊó∂‰∏çÂèØÁî®„ÄÇËØ∑Ê£ÄÊü•ÊÇ®ÁöÑÁΩëÁªúËøûÊé•ÊàñÁ®çÂêéÂÜçËØï„ÄÇ');
    } finally {
      setIsLoading(false);
    }
  };

  // ÈáçÊñ∞ÊäΩÁâå
  const handleRedraw = () => {
    setReading('');
    setShowCards(false);
    showCardSelection();
  };

  // ÈáçÁΩÆÂà∞ÂàùÂßãÁä∂ÊÄÅ
  const handleReset = () => {
    setSelectedTheme(null);
    setDrawnCards([]);
    setFlippedCards(new Set());
    setReading('');
    setIsLoading(false);
    setShowCards(false);
    setEmotionText('');
    setSelectedSpread('');
    setShowAllCards(false);
    setSelectedCardIds([]);
    setCardsConfirmed(false);
  };


  // ÁøªÂÆå‰∏âÂº†ÂêéËé∑ÂèñËß£ËØªÔºà‰∏çÂÜçÂºπÂá∫ÂØπËØùÊ°ÜÔºâ
  useEffect(() => {
    if (flippedCards.size === 3 && drawnCards.length === 3) {
      setTimeout(() => {
        getAIReading();
      }, 300);
    }
  }, [flippedCards, drawnCards]);

  // ÊÉÖÁª™ÊñáÊú¨ -> ÈòµÂºèÈÄâÊã©
  const inferSpreadFromEmotion = (text) => {
    const t = (text || '').toLowerCase();
    if (t.includes('ÁÑ¶Ëôë') || t.includes('‰∏çÂÆâ') || t.includes('Ëø∑Ëå´')) return '‰∏âÂº†ÁñóÊÑàÈòµ';
    if (t.includes('Áà±ÊÉÖ') || t.includes('ÂñúÊ¨¢') || t.includes('ÂàÜÊâã')) return 'ÊÅãÁà±‰∏âËßíÈòµ';
    if (t.includes('ËÄÉËØï') || t.includes('Â∑•‰Ωú') || t.includes('ÂéãÂäõ')) return 'ÊàêÈïøÈòµ';
    return '‰∏âÂº†Áâå';
  };

  // Ê†πÊçÆÈòµÂºèËá™Âä®Êé®Êñ≠Ëß£ËØªËßÜËßí
  const inferAnalysisPatternFromSpread = (spread) => {
    if (!spread) return 'ËøáÂéª-Áé∞Âú®-Êú™Êù•';
    if (spread.includes('ÊÅãÁà±')) return 'ËøáÂéª-Áé∞Âú®-Êú™Êù•';
    if (spread.includes('ÊàêÈïø')) return 'ÂéüÂõ†-Ë∑ØÂæÑ-ÁªìÊûú';
    if (spread.includes('ÁñóÊÑà')) return 'Áé∞Áä∂-ÈòªÁ¢ç-Ë∂ãÂäø';
    return 'ËøáÂéª-Áé∞Âú®-Êú™Êù•';
  };

  const handleGenerateByEmotion = () => {
    const spread = inferSpreadFromEmotion(emotionText);
    setSelectedSpread(spread);
    setAnalysisPattern(inferAnalysisPatternFromSpread(spread));
    // ‰∏çÂº∫Âà∂ÂÖ≥ËÅî‰∏ªÈ¢òÔºåÁî®Êà∑ÂèØ‰ª•ÈÄâÊã©‰∏ªÈ¢ò‰πüÂèØ‰ª•‰∏çÈÄâÊã©
    setReading('');
    showCardSelection();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-white via-indigo-50 to-pink-50 relative overflow-hidden">
      {/* ËÉåÊôØË£ÖÈ•∞ */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-pink-300/30 rounded-full blur-3xl"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-indigo-300/30 rounded-full blur-3xl"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-96 h-96 bg-purple-300/20 rounded-full blur-3xl"></div>
      </div>

      {/* ‰∏ªÊ†áÈ¢ò - Â∑¶‰∏äËßí */}
      <div className="absolute top-4 left-4 z-20">
        <h1 className="text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-purple-600 to-pink-600">
          ‚ú® AIÂ°îÁΩóÂç†Âçú ‚ú®
        </h1>
      </div>

      {/* ÊºîÁ§∫Ê®°ÂºèÊèêÁ§∫ */}
      {(!process.env.REACT_APP_OPENAI_API_KEY || 
        process.env.REACT_APP_OPENAI_API_KEY === 'your_openai_api_key_here') && (
        <div className="absolute top-4 right-4 z-20">
          <div className="inline-flex items-center glass-effect rounded-full px-4 py-2 text-sm text-gray-700">
            <span className="w-2 h-2 bg-yellow-400 rounded-full mr-2 animate-pulse"></span>
            ÊºîÁ§∫Ê®°Âºè - ÈÖçÁΩÆOpenAI API KeyÂêØÁî®AIËß£ËØª
          </div>
        </div>
      )}

      <div className="relative z-10 container mx-auto px-4 py-8 pt-[275px]">

        {/* ‰∏ªÈ¢òÈÄâÊã© */}
        {/* È¶ñÈ°µ‰∏ªÈ¢òÈÄâÊã©‰∏éÊèêÈóÆÂú®Âêå‰∏ÄÂ±èÔºåÈÄâ‰∏≠È´ò‰∫ÆÔºåÂçïÈÄâ‰∏çË∑≥ËΩ¨ */}
        <div className="max-w-4xl mx-auto mb-12">
          <h2 className="text-3xl font-semibold text-gray-800 text-center -mt-14 mb-8">
            ‰ªäÂ§©Êúâ‰ªÄ‰πàÂèØ‰ª•Â∏ÆÂà∞ÊÇ®Ôºü
          </h2>
          <div className="w-full grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-2 mb-1.5">
            {themes.map((theme) => (
              <ThemeButton
                key={theme.id}
                theme={theme}
                isSelected={selectedTheme?.id === theme.id}
                onClick={() => {
                  // ÁÇπÂáªÁõ∏Âêå‰∏ªÈ¢òÂàôÂèñÊ∂àÈÄâÊã©ÔºåÂê¶ÂàôÈÄâÊã©Êñ∞‰∏ªÈ¢ò
                  if (selectedTheme?.id === theme.id) {
                    setSelectedTheme(null);
                  } else {
                    setSelectedTheme(theme);
                  }
                }}
              />
            ))}
          </div>

          {/* È¶ñÈ°µ‰∏ªÈ¢ò‰∏ãÊñπÁöÑÊèêÈóÆËæìÂÖ•‰∏éÊåâÈíÆ */}
          <div className="max-w-4xl mx-auto mt-4 flex flex-col sm:flex-row items-stretch sm:items-center gap-3">
            <input
              value={emotionText}
              onChange={(e) => setEmotionText(e.target.value)}
              placeholder="ËØ∑ËæìÂÖ•‰Ω†ÊÉ≥ÈóÆÁöÑÈóÆÈ¢òÔºàÂèØÂåÖÂê´‰Ω†ÁöÑÊÉÖÁª™ÊàñÂú∫ÊôØÔºâ"
              className="flex-1 glass-effect rounded-xl px-4 py-3 text-gray-800 placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-400"
            />
            <div className="flex gap-2">
              <button
                onClick={handleGenerateByEmotion}
                className="rounded-xl px-4 py-3 bg-gradient-to-r from-purple-500 to-pink-500 text-white font-medium hover:opacity-90 transition shadow"
              >
                ÁîüÊàêÂ°îÁΩóÈòµ
              </button>
              <button
                onClick={handleReset}
                className="rounded-xl px-6 py-3 glass-effect text-gray-700 font-medium hover:bg-white/70 transition shadow"
              >
                ÈáçÁΩÆ
              </button>
            </div>
          </div>
        </div>

        {/* ÊâÄÊúâÁâåÂúÜÂΩ¢ÊéíÂàóÂ±ïÁ§∫Âå∫ÂüüÂíåÈÄâ‰∏≠ÁöÑ‰∏âÂº†Áâå */}
        {showAllCards && (
          <div className="max-w-7xl mx-auto mb-12">
            {!cardsConfirmed && (
              <div className="text-center mb-6">
                <p className="text-lg text-gray-700 mb-4">
                  ËØ∑‰ªé‰∏ãÊñπÈÄâÊã©‰∏âÂº†ÁâåÔºàÂ∑≤ÈÄâÊã© {selectedCardIds.length}/3Ôºâ
                </p>
                {selectedCardIds.length === 3 && (
                  <button
                    onClick={confirmCardSelection}
                    className="rounded-xl px-8 py-3 bg-gradient-to-r from-purple-500 to-pink-500 text-white font-medium hover:opacity-90 transition shadow-lg text-lg"
                  >
                    Á°ÆËÆ§ÈÄâÊã©Ëøô‰∏âÂº†Áâå
                  </button>
                )}
              </div>
            )}

            {/* ‰ΩøÁî® flex Â∏ÉÂ±ÄÔºåËÆ©ÂúÜÂΩ¢ÊéíÂàóÂíåÈÄâ‰∏≠ÁöÑ‰∏âÂº†ÁâåÂú®Âêå‰∏ÄË°å */}
            <div className={`flex flex-col ${cardsConfirmed ? 'lg:flex-row' : ''} items-center ${cardsConfirmed ? 'lg:items-start' : ''} justify-center gap-8`}>
              {/* ÂúÜÂΩ¢ÊéíÂàóÂå∫Âüü */}
              <div 
                className={`relative transition-all duration-500 ${
                  cardsConfirmed ? 'flex-shrink-0 lg:mr-8' : 'mx-auto'
                }`}
                style={{ 
                  width: cardsConfirmed ? '400px' : '700px', 
                  height: cardsConfirmed ? '400px' : '700px'
                }}
              >
                {tarotCards.map((card, index) => {
                  const totalCards = tarotCards.length;
                  const angle = (index / totalCards) * 2 * Math.PI - Math.PI / 2; // ‰ªéÈ°∂ÈÉ®ÂºÄÂßã
                  const radius = cardsConfirmed ? 140 : 250; // Ê†πÊçÆÁä∂ÊÄÅË∞ÉÊï¥ÂçäÂæÑ
                  const x = Math.cos(angle) * radius;
                  const y = Math.sin(angle) * radius;
                  const isSelected = selectedCardIds.includes(card.id);
                  const selectionIndex = isSelected ? selectedCardIds.indexOf(card.id) + 1 : null;

                  return (
                    <div
                      key={card.id}
                      onClick={() => !cardsConfirmed && handleCardSelect(card.id)}
                      className={`absolute transition-all duration-300 ${
                        cardsConfirmed ? 'cursor-default' : 'cursor-pointer'
                      } ${
                        isSelected ? 'scale-125 z-20' : cardsConfirmed ? 'scale-100' : 'hover:scale-110 z-10'
                      } ${!cardsConfirmed && selectedCardIds.length >= 3 && !isSelected ? 'opacity-40' : 'opacity-100'}`}
                      style={{
                        left: `calc(50% + ${x}px)`,
                        top: `calc(50% + ${y}px)`,
                        transform: 'translate(-50%, -50%)',
                        width: cardsConfirmed ? '60px' : '100px',
                        height: cardsConfirmed ? '90px' : '150px'
                      }}
                    >
                      <div className={`glass-effect p-1 rounded-lg h-full ${
                        isSelected ? 'ring-4 ring-purple-500 shadow-2xl' : 'shadow-lg'
                      }`}>
                        <div className="w-full h-full rounded-lg overflow-hidden">
                          <img 
                            src="/tarot-back.png" 
                            alt={card.name} 
                            className="w-full h-full object-cover"
                          />
                        </div>
                      </div>
                      {isSelected && !cardsConfirmed && (
                        <div className="absolute -top-3 -right-3 w-10 h-10 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full flex items-center justify-center text-white font-bold text-base shadow-lg border-2 border-white">
                          {selectionIndex}
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>

              {/* ÈÄâ‰∏≠ÁöÑ‰∏âÂº†ÁâåÂ±ïÁ§∫ */}
              {cardsConfirmed && showCards && drawnCards.length > 0 && (
                <div className="flex-shrink-0 flex items-center">
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    {drawnCards.map((card, index) => (
                      <div key={card.id} className="animate-slideUp" style={{ animationDelay: `${index * 0.2}s` }}>
                        <TarotCard
                          card={card}
                          isFlipped={flippedCards.has(card.id)}
                          onClick={() => handleCardFlip(card.id)}
                        />
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Â¶ÇÊûúÊ≤°ÊúâÊòæÁ§∫ÊâÄÊúâÁâåÔºå‰ΩÜÂ∑≤ÈÄâ‰∏≠‰∏âÂº†ÁâåÔºåÂàôÂçïÁã¨ÊòæÁ§∫ */}
        {!showAllCards && showCards && drawnCards.length > 0 && (
          <div className="max-w-6xl mx-auto mb-12">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {drawnCards.map((card, index) => (
                <div key={card.id} className="animate-slideUp" style={{ animationDelay: `${index * 0.2}s` }}>
                  <TarotCard
                    card={card}
                    isFlipped={flippedCards.has(card.id)}
                    onClick={() => handleCardFlip(card.id)}
                  />
                </div>
              ))}
            </div>

