import React, { useState, useEffect } from 'react';
import { Sparkles, RefreshCw, ArrowLeft } from 'lucide-react';
import ThemeButton from './components/ThemeButton';
import TarotCard from './components/TarotCard';
import ReadingResult from './components/ReadingResult';
import { themes, tarotCards } from './data/tarotCards';
import { OpenAIService } from './services/openaiService';
import { DemoService } from './services/demoService';

function App() {
  const [selectedTheme, setSelectedTheme] = useState(null);
  const [drawnCards, setDrawnCards] = useState([]);
  const [flippedCards, setFlippedCards] = useState(new Set());
  const [reading, setReading] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [showCards, setShowCards] = useState(false);
  const [emotionText, setEmotionText] = useState('');
  const [selectedSpread, setSelectedSpread] = useState('');
  const [analysisPattern, setAnalysisPattern] = useState('ËøáÂéª-Áé∞Âú®-Êú™Êù•');
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [followUpInput, setFollowUpInput] = useState('');
  const [showAllCards, setShowAllCards] = useState(false);
  const [selectedCardIds, setSelectedCardIds] = useState([]);

  // ÊòæÁ§∫ÊâÄÊúâÁâå‰æõÁî®Êà∑ÈÄâÊã©
  const showCardSelection = () => {
    // Êâì‰π±ÊâÄÊúâÁâåÁöÑÈ°∫Â∫è
    const shuffled = [...tarotCards].sort(() => Math.random() - 0.5);
    // ‰∏∫ÊØèÂº†ÁâåÊ∑ªÂä†ÈöèÊú∫Ê≠£ÈÄÜ‰Ωç
    const cardsWithReversed = shuffled.map(c => ({
      ...c,
      reversed: Math.random() < 0.5
    }));
    // ËøôÈáåÊàë‰ª¨‰∏çÈúÄË¶ÅÂ≠òÂÇ®ÊâÄÊúâÁâåÔºåÂè™ÈúÄË¶ÅÊ†áËÆ∞ÊòæÁ§∫Áä∂ÊÄÅ
    setShowAllCards(true);
    setSelectedCardIds([]);
    setDrawnCards([]);
    setFlippedCards(new Set());
    setShowCards(false);
  };

  // Áî®Êà∑ÈÄâÊã©‰∏ÄÂº†Áâå
  const handleCardSelect = (cardId) => {
    if (selectedCardIds.includes(cardId)) {
      // Â¶ÇÊûúÂ∑≤ÈÄâ‰∏≠ÔºåÂàôÂèñÊ∂àÈÄâÊã©
      setSelectedCardIds(prev => prev.filter(id => id !== cardId));
    } else if (selectedCardIds.length < 3) {
      // Â¶ÇÊûúÊú™ÈÄâ‰∏≠‰∏îÊú™ÈÄâÊª°3Âº†ÔºåÂàôÊ∑ªÂä†
      setSelectedCardIds(prev => [...prev, cardId]);
    }
  };

  // Á°ÆËÆ§ÈÄâÊã©‰∏âÂº†Áâå
  const confirmCardSelection = () => {
    if (selectedCardIds.length !== 3) return;

    const selected = selectedCardIds.map((cardId, idx) => {
