    if (t.includes('Áà±ÊÉÖ') || t.includes('ÂñúÊ¨¢') || t.includes('ÂàÜÊâã')) return 'ÊÅãÁà±‰∏âËßíÈòµ';
    if (t.includes('ËÄÉËØï') || t.includes('Â∑•‰Ωú') || t.includes('ÂéãÂäõ')) return 'ÊàêÈïøÈòµ';
    return '‰∏âÂº†Áâå';
  };

  // Ê†πÊçÆÈòµÂºèËá™Âä®Êé®Êñ≠Ëß£ËØªËßÜËßí
