            <span className="w-2 h-2 bg-yellow-400 rounded-full mr-2 animate-pulse"></span>
            ÊºîÁ§∫Ê®°Âºè - ÈÖçÁΩÆOpenAI API KeyÂêØÁî®AIËß£ËØª
          </div>
        </div>
      )}

      <div className="relative z-10 container mx-auto px-4 py-8 pt-[275px]">

        {/* ‰∏ªÈ¢òÈÄâÊã© */}
        {/* È¶ñÈ°µ‰∏ªÈ¢òÈÄâÊã©‰∏éÊèêÈóÆÂú®Âêå‰∏ÄÂ±èÔºåÈÄâ‰∏≠È´ò‰∫ÆÔºåÂçïÈÄâ‰∏çË∑≥ËΩ¨ */}
        <div className="max-w-4xl mx-auto mb-12">
          <h2 className="text-3xl font-semibold text-gray-800 text-center -mt-14 mb-8">
            ‰ªäÂ§©Êúâ‰ªÄ‰πàÂèØ‰ª•Â∏ÆÂà∞ÊÇ®Ôºü
          </h2>
          <div className="w-full grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-2 mb-1.5">
            {themes.map((theme) => (
              <ThemeButton
                key={theme.id}
                theme={theme}
                isSelected={selectedTheme?.id === theme.id}
                onClick={() => {
                  // ÁÇπÂáªÁõ∏Âêå‰∏ªÈ¢òÂàôÂèñÊ∂àÈÄâÊã©ÔºåÂê¶ÂàôÈÄâÊã©Êñ∞‰∏ªÈ¢ò
                  if (selectedTheme?.id === theme.id) {
                    setSelectedTheme(null);
                  } else {
                    setSelectedTheme(theme);
                  }
                }}
              />
            ))}
          </div>

          {/* È¶ñÈ°µ‰∏ªÈ¢ò‰∏ãÊñπÁöÑÊèêÈóÆËæìÂÖ•‰∏éÊåâÈíÆ */}
          <div className="max-w-4xl mx-auto mt-4 flex flex-col sm:flex-row items-stretch sm:items-center gap-3">
            <input
              value={emotionText}
              onChange={(e) => setEmotionText(e.target.value)}
              placeholder="ËØ∑ËæìÂÖ•‰Ω†ÊÉ≥ÈóÆÁöÑÈóÆÈ¢òÔºàÂèØÂåÖÂê´‰Ω†ÁöÑÊÉÖÁª™ÊàñÂú∫ÊôØÔºâ"
              className="flex-1 glass-effect rounded-xl px-4 py-3 text-gray-800 placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-400"
            />
