        )}

        {/* AIËß£ËØªÁªìÊûú */}
        {(reading || isLoading) && (
          <ReadingResult
            reading={reading}
            isLoading={isLoading}
            asDialog={false}
            header={{
              subtitle: `${selectedTheme ? selectedTheme.name : 'ÁªºÂêà‰∏ªÈ¢ò'} ¬∑ ${selectedSpread || '‰∏âÂº†Áâå'} ¬∑ ${emotionText || 'Êó†ÊÉÖÁª™ÊèèËø∞'}`,
              title: 'ÁªºÂêàËß£ËØª'
            }}
          />
        )}

        {/* Â∫ïÈÉ®Ë£ÖÈ•∞ */}
        <div className="text-center mt-16">
          <p className="text-gray-800 text-sm">
            ‚ú® Â°îÁΩóÁâåËß£ËØª‰ªÖ‰æõÂèÇËÄÉÔºåËØ∑‰ª•ÁßØÊûÅÁöÑÂøÉÊÄÅÈù¢ÂØπÁîüÊ¥ª ‚ú®
          </p>
        </div>
      </div>
    </div>
  );
}

